import React from 'react';
import './Scroll.css';
const Scroll=(props)=>{

    return (
        // Giving a className of ScrollHidden to hide the default Scroll Bar
        <div style={{overflow:'scroll', border:'3px solid black', height:'500px'}}className="scrollHidden">
            {props.children}
        </div>
    );
}
export default Scroll;
